query addresses($where: AddressWhereInput, $orderBy: AddressOrderByInput, $skip: Float, $take: Float) {
  items: addresses(where: $where, orderBy: $orderBy, skip: $skip, take: $take) {
    address_1
    address_2
    city
    createdAt
    id
    state
    updatedAt
    zip
    customers {
      id
      __typename
    }
    __typename
  }
  total: _addressesMeta(where: $where, orderBy: $orderBy, skip: $skip, take: $take) {
    count
    __typename
  }
}
